import { HomePage } from 'src/pages/HomePage'
import { MenCategory } from 'src/pages/MenCategory'

import React from 'react'
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom'
import Theme from './Theme'

const PRODUCTS_QUERY = `
{
  category {
    name
    products{
      id
      name
    }
  }
}
`

function App() {
  React.useEffect(() => {
    fetch('http://localhost:4000/graphql', {
      method: 'POST',
      headers: { 'content-type': 'application/json' },
      body: JSON.stringify({ query: PRODUCTS_QUERY }),
    })
      .then((response) => response.json())
      .then((data) => console.log(data))
  }, [])
  return (
    <Theme>
      <Router>
        <Routes>
          <Route exact path="/" element={<HomePage />} />

          {/* <Route
            exact
            path="/user/:id"
            render={({ match }) => (
              <ProductDescription user={clients.find((p) => p.id === match.params.id)} />
            )}
          /> */}
          {/* <Route exact path="/comparables" component={Comparables} />
          <Route exact path="/companyPage" component={CompanyPage} />
          <Route exact path="/searchResult" component={SearchResult} /> */}
        </Routes>
      </Router>
    </Theme>
  )
}

export default App
