import React, { Component } from 'react'
import PropTypes from 'prop-types'
import Tab from './Tab'
import styled from 'styled-components'
import logo from 'src/assets/images/logo.svg'
import cart from 'src/assets/images/cart.svg'

const Wrapper = styled.div`
  height: 80px;
  display: flex;

  header {
    padding: 5px 50px;
    height: auto;
    min-height: 90px;
    max-width: 1600px;
    margin: auto;
    display: flex;
  }
  .navbar {
    display: flex;
    gap: 10px;
    justify-content: space-between;
    align-items: center;
    width: 100%;
  }
  .navbar__tab-list {
    display: flex;
    list-style-type: none;
  }

  .navbar__logo {
    width: 32px;
    height: 28px;
  }
`
export class NavBar extends Component {
  static propTypes = {
    children: PropTypes.instanceOf(Array).isRequired,
  }

  constructor(props) {
    super(props)

    this.state = {
      activeTab: this.props.children[0].props.label,
    }
  }

  onClickTabItem = (tab) => {
    this.setState({ activeTab: tab })
  }

  render() {
    const {
      onClickTabItem,
      props: { children },
      state: { activeTab },
    } = this

    return (
      <Wrapper>
        <div className='container'>
          <header>
          <div className="navbar">
            <ul className="navbar__tab-list">
              {children.map((child) => {
                const { label } = child.props
                return (
                  <Tab activeTab={activeTab} key={label} label={label} onClick={onClickTabItem} />
                )
              })}
            </ul>
            <img className="navbar__logo" src={logo} alt="Brand logo" />
            <div className="navbar__currency-cart">
            <img className="navbar__logo" src={logo} alt="Brand logo" />

            </div>
          </div>
          </header>

          <div className="tab-content">
            {children.map((child) => {
              if (child.props.label !== activeTab) return undefined
              return child.props.children
            })}
          </div>
        </div>
      </Wrapper>
    )
  }
}

export default NavBar
