import React, { Component } from 'react'
import arrow from 'src/assets/images/arrow.svg'
import styled from 'styled-components'

const Wrapper = styled.div`
  position: relative;

  .navbar__currency-menu {
    display: flex;
    cursor: pointer;
    gap: 8px;
    align-items: baseline;

    .navbar_currency-toggler {
      width: 10px;
      height: 5px;
      &.isOpen {
        transform: rotate(-180deg);
      }
    }
  }
  .menu {
    position: absolute;
    width: 107px;
    height: auto;
    background-color: ${(props) => props.theme.colors.white};
    display: none;
    box-shadow: rgb(0 0 0 / 10%) 0px 10px 24px;
    &.isShowing {
      display: block;
      left: -18px;
      top: 30px;
    }

    .menu__list {
      list-style-type: none;
      li {
        padding: 21px;
      }
    }
  }
`
export class CurrrencyMenu extends Component {
  constructor(props) {
    super(props)
    this.state = {
      open: true,
    }
  }
  toggleCurrencyMenu = () => {
    const currentState = this.state.open
    this.setState({ open: !currentState })
  }
  render() {
    const {
      toggleCurrencyMenu,
      state: { open },
    } = this
    console.log(open)

    return (
      <Wrapper>
        <div className="navbar__currency-menu" onClick={toggleCurrencyMenu}>
          <p className="currency">$</p>
          <img
            className={`navbar_currency-toggler ${open ? 'isOpen' : ''}`}
            src={arrow}
            alt="arrow"
          />
        </div>
        <div className={`menu ${open ? 'isShowing' : null}`}>
          <ul className="menu__list">
            <li>$ USD</li>
            <li className="menu__list--a">€ EUR</li>
            <li>¥ JPY</li>
          </ul>
        </div>
      </Wrapper>
    )
  }
}

export default CurrrencyMenu
