import React, { Component } from 'react'
import PropTypes from 'prop-types'
import Tab from './Tab'
import styled from 'styled-components'
import logo from 'src/assets/images/logo.svg'
import { CurrrencyMenu } from 'src/components/CurrrencyMenu'
import { Cart } from 'src/components/Cart'
import arrow from 'src/assets/images/arrow.svg'

const Wrapper = styled.div`
  header {
    padding: 5px 50px;
    /* min-height: 80px; */
    display: flex;
  }
  .navbar {
    display: flex;
    gap: 10px;
    justify-content: space-between;
    align-items: center;
    width: 100%;
  }
  .navbar__tab-list {
    cursor: pointer;
    list-style-type: none;
    li {
      padding: 18px 15px;
    }
  }
  .navbar__tab-list.mobile {
    display: none;
  }
  .navbar__tab-list.desktop {
    display: flex;
  }
  .navbar__logo {
    width: 32px;
    height: 28px;
    cursor: pointer;
  }
  .navbar__currency-cart {
    display: flex;
    cursor: pointer;
    gap: 15px;
    align-items: center;
  }

  @media (max-width: ${(props) => props.theme.breakpoint.sm}) {
    .navbar__tab-list.desktop {
      display: none;
    }
    .navbar__tab-list.mobile {
      display: block;
    }

  .navbar__currency-menu {
    display: flex;
    cursor: pointer;
    gap: 8px;
    align-items: baseline;

    .navbar_currency-toggler {
      width: 10px;
      height: 5px;
      &.isOpen {
        transform: rotate(-180deg);
      }
    }
  }
  .menu {
    position: absolute;
    padding: 10px 0;
    min-width: 124px;
    height: auto;
    background-color: ${(props) => props.theme.colors.white};
    display: none;
    box-shadow: rgb(0 0 0 / 10%) 0px 10px 24px;
    &.isShowing {
      display: block;
      left: -24px;
      top: 30px;
    }

    .menu__list {
      list-style-type: none;

      li {
        padding: 12px 21px;
        font-size: 14px;
        &:hover {
          background-color: ${(props) => props.theme.colors.grey_02};
        }
      }
    }

    .menu__list--active {
      background-color: ${(props) => props.theme.colors.grey_01};
    }
  }
  }
`
export class NavBar extends Component {
  static propTypes = {
    children: PropTypes.instanceOf(Array).isRequired,
  }

  constructor(props) {
    super(props)
    this.state = {
      activeTab: this.props.children[0].props.label,
      isMenuOpen: false,

    }
  }

  onClickTabItem = (tab) => {
    this.setState({ activeTab: tab })
  }

  onClickLogo = () => {
    this.setState({ activeTab: 'all' })
  }

  toggleCategoryMenu = () => {
    const currentState = this.state.isMenuOpen
    this.setState({ isMenuOpen: !currentState })
  }

  render() {
    const {
      toggleCategoryMenu,
      onClickTabItem,
      onClickLogo,
      isMenuOpen,
      props: { children, client },
      state: { activeTab },
    } = this

    return (
      <Wrapper>
        <div className="container">
          <header>
            <div className="navbar">
              <div className="navbar__tab-list desktop">
                {children.map((child) => {
                  const { label } = child.props
                  return (
                    <Tab activeTab={activeTab} key={label} label={label} onClick={onClickTabItem} />
                  )
                })}
              </div>
              <ul className="navbar__tab-list mobile">
              <div className="navbar__currency-menu" onClick={toggleCategoryMenu}>
          <p className="currency">$</p>
          <img
            className={`navbar_currency-toggler ${isMenuOpen ? 'isOpen' : ''}`}
            src={arrow}
            alt="arrow"
          />
        </div>
        <div className={`menu ${isMenuOpen ? 'isShowing' : null}`}>
          <ul className="menu__list">
          {children.map((child) => {
                  const { label } = child.props
                  return (
                    <Tab activeTab={activeTab} key={label} label={label} onClick={onClickTabItem} />
                  )
                })}
          </ul>
        </div>

              </ul>

              <img onClick={onClickLogo} className="navbar__logo" src={logo} alt="Brand logo" />

              <div className="navbar__currency-cart">
                <CurrrencyMenu client={client} />
                <Cart />
              </div>
            </div>
          </header>

          <div className="tab-content">
            {children.map((child) => {
              if (child.props.label !== activeTab) return undefined
              return child.props.children
            })}
          </div>
        </div>
      </Wrapper>
    )
  }
}

export default NavBar
