            )}
          /> */}
        {/* <Route exact path="/comparables" component={Comparables} />
          <Route exact path="/companyPage" component={CompanyPage} />
          <Route exact path="/searchResult" component={SearchResult} /> */}
      </Routes>
    </Router>
  )
}

export default App
