import React, { Component } from 'react'
import PropTypes from 'prop-types'
import styled from 'styled-components'
const Wrapper = styled.div`
  margin-bottom: 40px;

  .carted-product__content {
    display: grid;
    grid-template-columns:  max-content 1fr;
    gap: 8px;
  }
  .carted-product__content-image {
    height: 190px;
    background-position: center;
    background-repeat: no-repeat;
    background-size: contain;
    max-width: 100%;
    position: relative;
  }
  .details {
    display: flex;
    flex-direction: column;
    gap: 5px;
    justify-content: space-between;

    .text {
      color: ${(props) => props.theme.colors.text_01};
      font-size: 16px;
      line-height: 160%;
    }
    .price {
      span {
        font-weight: 500;
      }
    }
  }
  .boxes {
    display: flex;
    gap: 6px;
    .box {
      border: 1px solid ${(props) => props.theme.colors.text_01};
      min-width: 35px;
      text-align: center;
      font-size: 14px;
      padding: 7px;
      &.active {
        background-color: ${(props) => props.theme.colors.dark};
        transition: 0.3s;
        color: ${(props) => props.theme.colors.white};
      }
    }
    .color {
      width: 23px;
      height: 23px;
      min-width: auto;
    }
  }
  .rhs{
      display:flex ;
  }
  .quantity {
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    align-items: center;

    button {
      border: 1px solid ${(props) => props.theme.colors.text_01};
      background-color: ${(props) => props.theme.colors.white};
      width: 23px;
      font-size: 31px;
      line-height: 11px;
      height: 23px;
      line-height: 11px;
      &:hover {
        cursor: pointer;
        background-color: ${(props) => props.theme.colors.dark};
        transition: 0.3s;
        color: ${(props) => props.theme.colors.white};
      }
    }
  }
`

export class CartedProduct extends Component {
  static propTypes = {
    product: PropTypes.object.isRequired,
    activeCurrencyLabel: PropTypes.string,
    fullCartPageStyle: PropTypes.bool,
  }
  constructor(props) {
    super(props)
    this.state = {
      productCount: 1,
    }
  }

  increaseProductCount = () => {
    this.setState({ productCount: this.state.productCount + 1 })
  }
  decreaseProductCount = () => {
    if (this.state.productCount > 1) {
      this.setState({ productCount: this.state.productCount - 1 })
    } else {
      this.setState({ productCount: this.state.productCount })
    }
  }
  componentDidMount = () => {
    this.props.client
      .query({
        query: gql`
          {
            category(input: { title: "${this.state.category}"}) {
              name
              products {
                id
                name
                inStock
                gallery
                description
                category
                attributes {
                  id
                  name
                  type
                  items {
                    displayValue
                    value
                    id
                  }
                }
                prices {
                  currency {
                    label
                    symbol
                  }
                  amount
                }
                brand
              }
            }
          }
        `,
      })
      .then((response) => {
        this.setState({ techProducts: response.data.category.products, isLoading: false })
      })
  }

  render() {
    const {
      increaseProductCount,
      decreaseProductCount,
      props: { client, activeCurrencyLabel, fullCartPageStyle=false },
      state: { productCount },
    } = this
    const filterCurrentAmount = product?.prices.find((obj) => {
      return obj.currency.label === activeCurrencyLabel
    })

    // var allAttributes =  product.attributes.map((attribute) =>
    //   attribute.items.map((color) => color.value)
    // )
    const allColors = product.attributes.find((attribute) => attribute.name === 'Color')

    const allCapacities = product.attributes.find((attribute) => attribute.id === 'Capacity')

    return (
      <Wrapper>
        <div className="carted-product__content">
          <div className="details">
            <p className="text">{product.name}</p>
            <div className="price">
              {' '}
              <span className="text">{filterCurrentAmount.currency.symbol}</span>
              {product.prices.some(
                (currentLabel) => currentLabel.currency.label === activeCurrencyLabel
              ) && <span className="text">{filterCurrentAmount.amount}</span>}
            </div>
            <div className="capacities">
              <p className="text">{allCapacities.id}:</p>
              <div className="boxes">
                {allCapacities?.items.map((capacity) => {
                  return (
                    <p key={capacity.value} className={` text box ${capacity.value === '1T' ? 'active' : ''}`}>
                      {capacity.value}
                    </p>
                  )
                })}
              </div>
            </div>
            <div className="colors">
              <p className="text">{allColors?.id}:</p>
              <div className="boxes">
                {allColors?.items.map((color) => {
                  return <p  key={color.value} style={{ backgroundColor: color.value }} className="box color"></p>
                })}
              </div>
            </div>
          </div>
       <div className="rhs">
       <div className="quantity">
            <button onClick={increaseProductCount}>+</button>
            <p className="count">{productCount}</p>
            <button onClick={decreaseProductCount}>-</button>
          </div>
          {/* <img src={product && product.gallery[0]} alt="" /> */}
          <div
            className="carted-product__content-image"
            style={{ backgroundImage: `url(${product && product.gallery[0]})` }}
          ></div>
       </div>
        </div>
      </Wrapper>
    )
  }
}

export default CartedProduct
