import React, { Component } from 'react'
import styled from 'styled-components'
import { gql } from '@apollo/client'

const Wrapper = styled.div``

const TECH_PRODUCTS_QUERY = `
{
    category(input: { title: "tech" }) {
      name
      products{
        id
      }

    }
  }
`

export class TechProducts extends Component {
  constructor(props) {
    super(props)
    this.state = {
      techProducts: [],
      isFetching: false,
    }
  }
  componentDidMount = () => {
    this.props.client
      .query({
        query: gql`
        {
            category(input: { title: "tech" }) {
              name
              products{
                id
              }

            }
          }
        `,
      })
      .then((response) => {
        this.setState({ techProducts: response })
      })
  }

  render() {
    const {
      state: { isFetching, techProducts },
    } = this
    console.log(techProducts)

    return (
      <Wrapper>
        {isFetching ? (
          <p>Loading...</p>
        ) : (
          <div>
            ALL_PRODUCTS_QUERY
            {JSON.stringify(techProducts)}
          </div>
        )}
      </Wrapper>
    )
  }
}

export default TechProducts
