import React, { Component } from 'react'
import styled from 'styled-components'
import cart from 'src/assets/images/cart.svg'
import { gql } from '@apollo/client'

const Wrapper = styled.div`
  .navbar__cart {
    position: relative;

    img {
      width: 27px;
      height: 20px;
    }

    .navbar__cart--items {
      background-color: ${(props) => props.theme.colors.dark};
      color: ${(props) => props.theme.colors.white};
      border-radius: 50%;
      width: 18px;
      justify-content: center;
      font-weight: 500;
      align-items: flex-start;
      height: 18px;
      display: flex;
      font-size: 12px;
      position: absolute;
      right: -8px;
      top: -7px;
    }
  }
  .menu {
    position: absolute;
    padding: 32px 16px;
    min-width: 325px;
    height: auto;
    background-color: ${(props) => props.theme.colors.white};
    display: none;
    box-shadow: rgb(0 0 0 / 10%) 0px 10px 24px;
    &.isShowing {
      display: block;
      right: 22px;
      top: 67px;
    }

    .heading{
      margin-bottom: ;
.bold{
  font-weight: bold ;
}
    }
  }
`
export class Cart extends Component {
  constructor(props) {
    super(props)
    this.state = {
      allProducts: [],
      isLoading: true,
      isMenuOpen: true,
    }
  }

  toggleCartMenu = () => {
    const currentState = this.state.isMenuOpen
    this.setState({ isMenuOpen: !currentState })
  }

  componentDidMount = () => {
    this.props.client
      .query({
        query: gql`
          {
            category {
              name
              products {
                id
                name
                inStock
                gallery
                description
                category
                attributes {
                  id
                  name
                  type
                  items {
                    displayValue
                    value
                    id
                  }
                }
                prices {
                  currency {
                    label
                    symbol
                  }
                  amount
                }

                brand
              }
            }
          }
        `,
      })
      .then((response) => {
        this.setState({ allProducts: response.data.category.products, isLoading: false })
      })
  }

  render() {
    const {
      toggleCartMenu,

      state: { isLoading, isMenuOpen, allProducts },
    } = this
    const cartedProducts = allProducts.slice(0, 3)
    console.log(cartedProducts)

    return (
      <Wrapper>
        <div className="navbar__cart" onClick={toggleCartMenu}>
          <img src={cart} alt="Brand cart" />
          <p className="navbar__cart--items">{cartedProducts.length}</p>
        </div>

        <div className={`menu ${isMenuOpen ? 'isShowing' : null}`}>
          {isLoading ? (
            <p>Loading...</p>
          ) : (
            <div>
              <p className="heading">
                <span className="bold">My Bag, </span>
                <span className="light">     {cartedProducts.length} items</span>
              </p>
            </div>
          )}
        </div>
      </Wrapper>
    )
  }
}

export default Cart
