import PropTypes from 'prop-types'
import React, { Component } from 'react'
import { gql } from '@apollo/client'

export class SingleProduct extends Component {
  static propTypes = {}

  constructor(props) {
    super(props)
    this.state = {
      category: 'clothes',
    }
  }

  componentDidMount = () => {
    this.props.client
      .query({
        query: gql`
          {
            category(input: { title: "${this.state.category}"}) {
             name
              products {
                id
                name
                inStock
                gallery
                description
                category
                attributes {
                  id
                  name
                  type
                  items {
                    displayValue
                    value
                    id
                  }
                }
                prices {
                  currency {
                    label
                    symbol
                  }
                  amount
                }

                brand
              }
            }
          }
        `,
      })
      .then((response) => {
        console.log(response)
      })
  }

  render() {
    return (
      <div>SingleProduct</div>
    )
  }
}

export default SingleProduct
