import React, { Component } from 'react'
import styled from 'styled-components'
import { gql } from '@apollo/client'

const Wrapper = styled.div``

export class TechProducts extends Component {
  constructor(props) {
    super(props)
    this.state = {
      techProducts: [],
      isFetching: false,
    }
  }
  componentDidMount = () => {
    const { client } = this.props

    client
    .query({
        query: gql`
        {
            category(input: { title: "clothes" }) {
              name
              products {
                id
                name
                inStock
                gallery
                description
                category
                attributes {
                  id
                  name
                  type
                  items {
                    displayValue
                    value
                    id
                  }
                }
                prices {
                  currency {
                    label
                    symbol
                  }
                  amount
                }

                brand
              }
            }
          }
        `,
      })
      .then((response) => {
        this.setState({ techProducts: response })
      })
  }

  render() {
    const {
      state: { isFetching, techProducts },
    } = this
    console.log(techProducts)

    return (
      <Wrapper>
        {isFetching ? (
          <p>Loading...</p>
        ) : (
          <div>
            {JSON.stringify(techProducts)}
          </div>
        )}
      </Wrapper>
    )
  }
}

export default TechProducts
