import React, { Component } from 'react'
import arrow from 'src/assets/images/arrow.svg'
import styled from 'styled-components'

const Wrapper = styled.div`
  .navbar__currency-menu {
    display: flex;
    cursor: pointer;
    gap: 8px;
    align-items: baseline;

    .navbar_currency-toggler {
      width: 10px;
      height: 5px;
    }
  }
`
export class CurrrencyMenu extends Component {
    addClassName =() =>{
        this.setState({categorie: "newClass"});
        console.log(this.state.categorie);
    }
  render() {
    return (
      <Wrapper>
        <div className="navbar__currency-menu">
          <p className="currency">$</p>
          <img className={`this.state.categorie`} src={arrow} alt="arrow" />
        </div>
      </Wrapper>
    )
  }
}

export default CurrrencyMenu
