import React, { Component } from 'react'
import styled from 'styled-components'
import { gql } from '@apollo/client'

const Wrapper = styled.div``

export class TechProducts extends Component {
  constructor(props) {
    super(props)
    this.state = {
      techProducts: [],
      isLoading: true,
    }
  }
  componentDidMount = () => {
    const { client } = this.props

    client
      .query({
        query: gql`
          {
            category(input: { title: "tech" }) {
              name
              products {
                id
                name
                inStock
                gallery
                description
                category
                attributes {
                  id
                  name
                  type
                  items {
                    displayValue
                    value
                    id
                  }
                }
                prices {
                  currency {
                    label
                    symbol
                  }
                  amount
                }

                brand
              }
            }
          }
        `,
      })
      .then((response) => {
        this.setState({ techProducts: response, isLoading: false })
      })
  }

  render() {
    const {
      state: { isLoading, techProducts },
    } = this
    console.log(techProducts)

    return (
      <Wrapper>
        {isLoading ? <p>Loading...</p> : <div>techProducts</div>}
      </Wrapper>
    )
  }
}

export default TechProducts
