import React, { Component } from 'react'
import styled from 'styled-components'
import { gql } from '@apollo/client'
import { CartedProduct } from 'src/components/CartedProduct'
import PropTypes from 'prop-types'
import { NavBar } from 'src/components/NavBar'

const Wrapper = styled.div`
  .content {
    padding: 5px 50px;
    margin: 125px 0 50px 0;
    @media (max-width: ${(props) => props.theme.breakpoint.sm}) {
      padding: 5px 25px;
    }
.heading{
  margin-bottom:55px ;
  border-bottom: ;
  .bold{
    font-weight:bold ;
  }
}
  }


`
export class CartPage extends Component {
  static propTypes = {
    activeCurrencyLabel: PropTypes.string,
  }

  constructor(props) {
    super(props)
    this.state = {
      techProducts: [],
      isLoading: true,
      isMenuOpen: false,
      category: 'tech',
      activeCurrencyLabel: 'AUD',
    }
  }

  toggleCartMenu = () => {
    const currentState = this.state.isMenuOpen
    this.setState({ isMenuOpen: !currentState })
  }

  componentDidMount = () => {
    this.props.client
      .query({
        query: gql`
          {
            category(input: { title: "${this.state.category}"}) {
              name
              products {
                id
                name
                inStock
                gallery
                description
                category
                attributes {
                  id
                  name
                  type
                  items {
                    displayValue
                    value
                    id
                  }
                }
                prices {
                  currency {
                    label
                    symbol
                  }
                  amount
                }
                brand
              }
            }
          }
        `,
      })
      .then((response) => {
        this.setState({ techProducts: response.data.category.products, isLoading: false })
      })
  }

  onCurrencyMenuClick = (e) => {
    this.setState({ activeCurrencyLabel: e.currentTarget.textContent.split(' ')[1] })
  }

  render() {
    const {
      onCurrencyMenuClick,
      props: { client },

      state: { isLoading, isMenuOpen, activeCurrencyLabel, techProducts },
    } = this
    const cartedProducts = techProducts.slice(0, 3)

    return (
      <Wrapper>
        <>
        <NavBar
          client={client}
          activeCurrencyLabel={activeCurrencyLabel}
          onCurrencyMenuClick={onCurrencyMenuClick}
        />
          <div className="content">
            <p className="heading">
              <span className="bold">CART</span>
            </p>
            {isLoading ? (
              <p>Loading...</p>
            ) : (
              <div>
                {cartedProducts.map((product) => {
                  return (
                    <CartedProduct
                      key={product.id}
                      product={product}
                      activeCurrencyLabel={activeCurrencyLabel}
                    />
                  )
                })}
              </div>
            )}
          </div>
          <div className="total">
            <p>Total</p>
            <p>$700.00</p>
          </div>

        </>
      </Wrapper>
    )
  }
}

export default CartPage
