import React, { Component } from 'react'
import PropTypes from 'prop-types'
import styled from 'styled-components'

const Wrapper = styled.div`
  box-shadow: 0px 4px 35px rgba(168, 172, 176, 0.19);
  background-color: ${(props) => props.theme.colors.white};
  padding: 10px;
  display: flex;
  flex-direction: column;
  justify-content: space-between;

  .product-card__content-image {
    height: 330px;
    background-position: center;
    background-repeat: no-repeat;
    background-size: contain;
    max-width: 100%;

    .stock--overlay {
      text-align: center;
      background-color: ${(props) => props.theme.colors.bg_overlay};
      display: flex;
      height: 100%;
      width: 100%;
      justify-content: center;
      align-items: center;
      p {
        font-size: 20px;
        line-height: 160%;
        color: ${(props) => props.theme.colors.text_02};
      }
    }
  }

  .product-card__content-text {
    margin-top: 10px;
    p {
      font-size: 15px;
      line-height: 160%;
      color: ${(props) => props.theme.colors.text_02};
      font-weight: 400;
    }
    .product-card__content-text--price {
      font-weight: 500;
      color: ${(props) => props.theme.colors.text_01};
    }
  }
`

export class ProductCard extends Component {
  static propTypes = {
    product: PropTypes.object.isRequired,
    activeCurrencyLabel: PropTypes.string,
  }

  render() {
    const {
      props: { product,activeCurrencyLabel },
    } = this
    const filterCurrentAmount = product.prices.find(obj => {
        return obj.currency.label === activeCurrencyLabel
      })
console.log(filterCurrentAmount.amount)

return (
      <Wrapper>
        <div className="product-card__content">
          <div
            className="product-card__content-image"
            style={{ backgroundImage: `url(${product.gallery[0]})` }}
          >
            {' '}
            {product.inStock && (
              <div className="stock--overlay">
                <p>OUT OF STOCK</p>
              </div>
            )}
          </div>
          <div className="product-card__content-text">
            <p className="product-card__content-text--name">{product.name}</p>
            <p className="product-card__content-text--price">                    {activeCurrencyLabel}
</p>

          </div>
        </div>
      </Wrapper>
    )
  }
}

export default ProductCard
