import React from 'react'
import { ThemeProvider, createGlobalStyle } from 'styled-components'
import PropTypes from 'prop-types'

const theme = {
  colors: {
    text_01: "#1D1F22",
    primary_color: "#5ECE7B",
    // ui_02: "#2EC4B6",
  },
  breakpoint: {
    lg: '1058px',
    md: '768px',
    sm: '480px',
  },
}

const GlobalStyle = createGlobalStyle`
@font-face {
  font-family: 'Satoshi';
  src: url('./assets/fonts/Satoshi-Regular.otf');
  font-style: normal;
  font-weight: 400;
}
/* 
@font-face {
  font-family: 'Raleway';
  src: url('./assets/fonts/Raleway-Medium.ttf');
  font-style: normal;
  font-weight: 500;
}

@font-face {
  font-family: 'Raleway';
  src: url('./assets/fonts/Raleway-Bold.ttf');
  font-style: normal;
  font-weight: bold;
} */
html{
  scroll-behavior: smooth;
}


img{
  width: 100%;
  height: auto;
}

html,
body,
h1,
h2,
h3,
h4,
h5,
h6,
p,
input,
button, ul {
  padding: 0;
  margin: 0;
  font-weight: 400;
  font-family: Satoshi, sans-serif;
}

.container{
  max-width: 1600px;
    margin: auto;
    width: 100%;

}

a {
  color: inherit;
  text-decoration: none;
}

* {
  box-sizing: border-box;
	}
`

const Theme = (props) => (
  <ThemeProvider theme={theme}>
    <div>
      <GlobalStyle />
      {props.children}
    </div>
  </ThemeProvider>
)
Theme.propTypes = {
  children: PropTypes.any.isRequired,
}

export { theme }
export default Theme
