import React, { Component } from 'react'
import styled from 'styled-components'
import cart from 'src/assets/images/cart.svg'

const Wrapper = styled.div`
  .navbar__cart {
    position: relative;

    img {
      width: 27px;
      height: 20px;
    }

    .navbar__cart--items {
      background-color: ${(props) => props.theme.colors.dark};
      color: ${(props) => props.theme.colors.white};
      border-radius: 50%;
      width: 18px;
      justify-content: center;
      font-weight: 500;
      align-items: flex-start;
      height: 18px;
      display: flex;
      font-size: 12px;
      position: absolute;
      right: -8px;
      top: -7px;
    }
  }
`
export class Cart extends Component {
  constructor(props) {
    super(props)
    this.state = {
      isMenuOpen: false,
    }
  }

  toggleCartMenu = () => {
    const currentState = this.state.isMenuOpen
    this.setState({ isMenuOpen: !currentState })
  }
  render() {
    const {
      toggleCartMenu,

      state: { isMenuOpen },
    } = this
    return (
      <Wrapper>
        <div className="navbar__cart" onClick={toggleCartMenu}>
          <img src={cart} alt="Brand cart" />
          <p className="navbar__cart--items">3</p>
        </div>
        <div className={`menu ${isMenuOpen ? 'isShowing' : null}`}></div>
      </Wrapper>
    )
  }
}

export default Cart
