// import PropTypes from 'prop-types'
import React, { Component } from 'react'
import { gql } from '@apollo/client'
import { NavBar } from 'src/components/NavBar'
import { SingleProduct } from 'src/components/SingleProduct'
import styled from 'styled-components'

const Wrapper = styled.div`
  .content {
    padding: 5px 50px;
    margin: 125px 0 50px 0;
    @media (max-width: ${(props) => props.theme.breakpoint.sm}) {
      padding: 5px 25px;
    }
  }
  `
export class SingleProductPage extends Component {
  constructor(props) {
    super(props)
    this.state = {
      singleProduct: {},
      isLoading: true,
      activeCurrencyLabel: 'AUD',

    }
  }
  onCurrencyMenuClick = (e) => {
    this.setState({ activeCurrencyLabel: e.currentTarget.textContent.split(' ')[1] })
  }

  componentDidMount = () => {
    const searchParams = new URLSearchParams(window.location.search)
    const paramsObj = Object.fromEntries(searchParams)

    this.props.client
      .query({
        query: gql`
        {
          product(id: "${paramsObj.id}") {
            id
            name
            inStock
            gallery
            description
            category
            attributes {
              id
              name
              type
              items {
                displayValue
                value
                id
              }
            }
            prices {
              currency {
                label
                symbol
              }
              amount
            }

            brand
          }
        }`,
      })
      .then((response) => {
        this.setState({ singleProduct: response.data.product, isLoading: false })
      })
  }

  render() {
    const {
      onCurrencyMenuClick,
      props: { client },
      state: { activeCurrencyLabel },
    } = this

    return (
      <div>
        <NavBar
          client={client}
          activeCurrencyLabel={activeCurrencyLabel}
          onCurrencyMenuClick={onCurrencyMenuClick}
        />
        <div className="content">
          <SingleProduct client={client} />
        </div>
      </div>
    )
  }
}

export default SingleProductPage
