import React, { Component } from 'react'
import styled from 'styled-components'

const Wrapper = styled.div``

const ALL_PRODUCTS_QUERY = `
{
    category {
      name
      products{
        id
        name
        category
      }
    }
  }
`

export class AllProducts extends Component {
  constructor(props) {
    super(props)
    this.state = {
      allProducts: [],
      isFetching: false,
    }
  }

  componentDidMount = () => {
    this.props.client
      .query({
        query: gql`
          {
            currencies {
              label
              symbol
            }
          }
        `,
      })
      .then((response) => {
        this.setState({ currencies: response.data.currencies })
      })
  }

  render() {
    const {
      state: { isFetching, allProducts },
    } = this
    console.log(isFetching)

    return (
      <Wrapper>
        {isFetching ? (
          <p>Loading...</p>
        ) : (
          <div>
            ALL_PRODUCTS_QUERY
            {JSON.stringify(allProducts)}
          </div>
        )}
      </Wrapper>
    )
  }
}

export default AllProducts
