import { PureComponent } from 'react'
import { NavBar } from 'src/components/NavBar'
import { gql } from '@apollo/client'
import loading from 'src/assets/images/loading.gif'
import styled from 'styled-components'

const Wrapper = styled.div`
  .content {
    padding: 5px 50px;
    margin: 125px 0 50px 0;
    @media (max-width: ${(props) => props.theme.breakpoint.sm}) {
      padding: 5px 25px
    }
  }
  .products--grid {
    display: grid;
    gap: 40px;
    grid-template-columns: repeat(auto-fill, minmax(260px, 1fr));
  }
  .loading {
    display: flex;
    justify-content: center;
    align-items: center;
    width: 100%;
    height: calc(100vh - 130px);
    img {
      width: 100px;
    }
  }
`

export class CartPage extends PureComponent {
  constructor(props) {
    super(props)
    this.state = {
      activeCurrencyLabel: 'AUD',
      productCount: 1,
      category:"tech"

    }
  }
  onCurrencyMenuClick = (e) => {
    this.setState({ activeCurrencyLabel: e.currentTarget.textContent.split(' ')[1] })

  }
  increaseProductCount = () => {
    this.setState({ productCount: this.state.productCount + 1 })
  }
  decreaseProductCount = () => {
    if (this.state.productCount > 1) {
      this.setState({ productCount: this.state.productCount - 1 })
    } else {
      this.setState({ productCount: this.state.productCount })
    }
  }
  componentDidMount = () => {
    this.props.client
      .query({
        query: gql`
          {
            category(input: { title: "${this.state.category}"}) {
              name
              products {
                id
                name
                inStock
                gallery
                description
                category
                attributes {
                  id
                  name
                  type
                  items {
                    displayValue
                    value
                    id
                  }
                }
                prices {
                  currency {
                    label
                    symbol
                  }
                  amount
                }
                brand
              }
            }
          }
        `,
      })
      .then((response) => {
        this.setState({ techProducts: response.data.category.products, isLoading: false })
      })
  }


  render() {
    const {
      increaseProductCount,
      decreaseProductCount,
      onCurrencyMenuClick,
      props: { client },
      state: { activeCurrencyLabel,productCount,techProducts,isLoading },
    } = this

    const product = techProducts?.slice(0, 3)
    const filterCurrentAmount = product?.prices &&product?.prices.find((obj) => {
      return obj.currency.label === activeCurrencyLabel
    })

    // var allAttributes =  product.attributes.map((attribute) =>
    //   attribute.items.map((color) => color.value)
    // )
    const allColors = product?.attributes&&product?.attributes.find((attribute) => attribute.name === 'Color')

    const allCapacities = product?.attributes&&product?.attributes.find((attribute) => attribute.id === 'Capacity')
console.log(product)

    return (
      <Wrapper>
        <NavBar
        showCategoryTab={false}
          client={client}
          activeCurrencyLabel={activeCurrencyLabel}
          onCurrencyMenuClick={onCurrencyMenuClick}
        />
         {
          isLoading ? (
            <div className="loading">
              <img src={loading} alt="loading..." />
            </div>):( <div className="carted-product__content">
          <div className="details">
            <p className="text">{product?.name}</p>
            <div className="price">
              {' '}
              <span className="text">{filterCurrentAmount?.currency?.symbol}</span>
              {product?.prices?.some(
                (currentLabel) => currentLabel.currency.label === activeCurrencyLabel
              ) && <span className="text">{filterCurrentAmount.amount}</span>}
            </div>
            <div className="capacities">
              <p className="text">{allCapacities?.id}:</p>
              <div className="boxes">
                {allCapacities?.items.map((capacity) => {
                  return (
                    <p className={` text box ${capacity.value === '1T' ? 'active' : ''}`}>
                      {capacity.value}
                    </p>
                  )
                })}
              </div>
            </div>
            <div className="colors">
              <p className="text">{allColors?.id}:</p>
              <div className="boxes">
                {allColors?.items.map((color) => {
                  return <p style={{ backgroundColor: color.value }} className="box color"></p>
                })}
              </div>
            </div>
          </div>
          <div className="quantity">
            <button onClick={increaseProductCount}>+</button>
            <p className="count">{productCount}</p>
            <button onClick={decreaseProductCount}>-</button>
          </div>
          {/* <div
            className="carted-product__content-image"
            style={{ backgroundImage: `url(${product && product.gallery[0]})` }}
          ></div> */}
        </div>)
        } 

      </Wrapper>
    )
  }
}

export default CartPage
