import React, { Component } from 'react'
import styled from 'styled-components'
import { gql } from '@apollo/client'
import { ProductCard } from 'src/components/ProductCard'
import PropTypes from 'prop-types'

const Wrapper = styled.div`
  .products--grid {
    display: grid;
    gap: 40px;
    grid-template-columns: repeat(auto-fill, minmax(249px, 1fr));
  }
`

export class TechProducts extends Component {
    static propTypes = {
        product: PropTypes.object.isRequired,
        activeCurrencyLabel: PropTypes.string,
      }

  constructor(props) {
    super(props)
    this.state = {
      techProducts: [],
      isLoading: true,
    }
  }

  componentDidMount = () => {
    this.props.client
      .query({
        query: gql`
          {
            category(input: { title: "tech" }) {
              name
              products {
                id
                name
                inStock
                gallery
                description
                category
                attributes {
                  id
                  name
                  type
                  items {
                    displayValue
                    value
                    id
                  }
                }
                prices {
                  currency {
                    label
                    symbol
                  }
                  amount
                }

                brand
              }
            }
          }
        `,
      })
      .then((response) => {
        this.setState({ techProducts: response, isLoading: false })
      })
  }

  render() {
    const {
        props: { activeCurrencyLabel },

      state: { isLoading, techProducts },
    } = this

    return (
      <Wrapper>
        {isLoading ? (
          <p>Loading...</p>
        ) : (
          <div className="products--grid">
            {techProducts.map((product) => {
              return <ProductCard key={product.id} product={product} activeCurrencyLabel={activeCurrencyLabel} />
            })}
          </div>
        )}
      </Wrapper>
    )
  }
}

export default TechProducts
