import React, { Component } from 'react'
import styled from 'styled-components'
import cart from 'src/assets/images/cart.svg'
import { gql } from '@apollo/client'
import { CartedProduct } from 'src/components/CartedProduct'
import PropTypes from 'prop-types'
import { Link } from 'react-router-dom'
import { NavBar } from 'src/components/NavBar'

const Wrapper = styled.div`


    .menu-content {
      max-height: 400px;

      overflow-y: scroll;

      .heading {
        margin-bottom: 32px;
        .bold {
          font-weight: bold;
        }
      }
    }
    .total {
      margin: 32px 0;
      display: flex;
      justify-content: space-between;
      p {
        font-weight: 600;
      }
    }
    .menu-btns {
      display: grid;
      grid-template-columns: 1fr 1fr;
      gap: 10px;
      button {
        height: 43px;
        padding: 13px;
        outline: none;
        cursor: pointer;
        width: 100%;
      }
      .view-bag {
        background-color: ${(props) => props.theme.colors.white};
        color: ${(props) => props.theme.colors.text_01};
        border: 1px solid ${(props) => props.theme.colors.text_01};
        &:hover {
          background-color: ${(props) => props.theme.colors.text_01};
          color: ${(props) => props.theme.colors.white};
          transition: 0.3s;
        }
      }
      .checkout {
        background-color: ${(props) => props.theme.colors.primary_color};
        color: ${(props) => props.theme.colors.white};
        border: 1px solid ${(props) => props.theme.colors.primary_color};
        &:hover {
          background-color: ${(props) => props.theme.colors.white};
          color: ${(props) => props.theme.colors.primary_color};
          transition: 0.3s;
        }
      }
    }


`
export class Cart extends Component {
  static propTypes = {
    activeCurrencyLabel: PropTypes.string,
  }

  constructor(props) {
    super(props)
    this.state = {
      techProducts: [],
      isLoading: true,
      isMenuOpen: false,
      category: 'tech',
    }
  }

  toggleCartMenu = () => {
    const currentState = this.state.isMenuOpen
    this.setState({ isMenuOpen: !currentState })
  }

  componentDidMount = () => {
    this.props.client
      .query({
        query: gql`
          {
            category(input: { title: "${this.state.category}"}) {
              name
              products {
                id
                name
                inStock
                gallery
                description
                category
                attributes {
                  id
                  name
                  type
                  items {
                    displayValue
                    value
                    id
                  }
                }
                prices {
                  currency {
                    label
                    symbol
                  }
                  amount
                }
                brand
              }
            }
          }
        `,
      })
      .then((response) => {
        this.setState({ techProducts: response.data.category.products, isLoading: false })
      })
  }

  render() {
    const {
      toggleCartMenu,
      props: { activeCurrencyLabel },

      state: { isLoading, isMenuOpen, techProducts },
    } = this
    const cartedProducts = techProducts.slice(0, 3)

    return (
      <Wrapper>
         <NavBar
          client={client}
          activeCurrencyLabel={activeCurrencyLabel}
          onCurrencyMenuClick={onCurrencyMenuClick}
        >
        <div >
          <div className="menu-content">
            <p className="heading">
              <span className="bold">My Bag, </span>
              <span className="light"> {cartedProducts.length} items</span>
            </p>
            {isLoading ? (
              <p>Loading...</p>
            ) : (
              <div>
                {cartedProducts.map((product) => {
                  return (
                    <CartedProduct
                      key={product.id}
                      product={product}
                      activeCurrencyLabel={activeCurrencyLabel}
                    />
                  )
                })}
              </div>
            )}
          </div>
          <div className="total">
            <p>Total</p>
            <p>$700.00</p>
          </div>
          <div className="menu-btns">
            <Link to="/cart">
              <button className="view-bag">VIEW BAG</button>
            </Link>
            <button className="checkout">CHECKOUT</button>
          </div>
        </div>
        </NavBar>

      </Wrapper>
    )
  }
}

export default Cart
