import React, { Component } from 'react'
import PropTypes from 'prop-types'
import Tab from './Tab'
import styled from 'styled-components'
import logo from 'src/assets/images/logo.svg'
import { CurrrencyMenu } from 'src/components/CurrrencyMenu'
import { Cart } from 'src/components/Cart'
// import { Link } from 'react-router-dom';

const Wrapper = styled.div`
  header {
    padding: 5px 50px;
    /* min-height: 80px; */
    display: flex;
  }
  .navbar {
    display: flex;
    gap: 10px;
    justify-content: space-between;
    align-items: center;
    width: 100%;
  }
  .navbar__tab-list {
    display: flex;
    cursor: pointer;
    list-style-type: none;
    li {
      padding: 18px 15px;
    }
  }

  .navbar__logo {
    width: 32px;
    height: 28px;
    cursor: pointer;
  }
  .navbar__currency-cart {
    display: flex;
    cursor: pointer;
    gap: 15px;
    align-items: center;
  }
`
export class NavBar extends Component {
  static propTypes = {
    children: PropTypes.instanceOf(Array).isRequired,
  }

  constructor(props) {
    super(props)

    this.state = {
      activeTab: this.props.children[0].props.label,
    }
  }

  onClickTabItem = (tab) => {
    this.setState({ activeTab: tab })
  }

  onClickLogo = () => {
    this.setState({ activeTab: 'WOMEN' })
  }

  render() {
    const {
      onClickTabItem,
      onClickLogo,
      props: { children },
      state: { activeTab },
    } = this
    console.log(activeTab)

    return (
      <Wrapper>
        <div className="container">
          <header>
            <div className="navbar">
              <ul className="navbar__tab-list">
                {children.map((child) => {
                  const { label } = child.props
                  return (
                    <Tab activeTab={activeTab} key={label} label={label} onClick={onClickTabItem} />
                  )
                })}
              </ul>

              <img onClick={onClickLogo} className="navbar__logo" src={logo} alt="Brand logo" />

              <div className="navbar__currency-cart">
                <CurrrencyMenu />
                <Cart />
              </div>
            </div>
          </header>

          <div className="tab-content">
            {children.map((child) => {
              if (child.props.label !== activeTab) return undefined
              return child.props.children
            })}
          </div>
        </div>
      </Wrapper>
    )
  }
}

export default NavBar
