import { PureComponent } from 'react'
import { NavBar } from 'src/components/NavBar'
import { CartedProduct } from 'src/components/CartedProduct'
import { gql } from '@apollo/client'

import styled from 'styled-components'

const Wrapper = styled.div`
  .content {
    padding: 5px 50px;
    margin: 125px 0 50px 0;
    @media (max-width: ${(props) => props.theme.breakpoint.sm}) {
      padding: 5px 25px;
    }
  }
  .products--grid {
    display: grid;
    gap: 40px;
    grid-template-columns: repeat(auto-fill, minmax(260px, 1fr));
  }
  .loading {
    display: flex;
    justify-content: center;
    align-items: center;
    width: 100%;
    height: calc(100vh - 130px);
    img {
      width: 100px;
    }
  }
`

export class CartPage extends PureComponent {
  constructor(props) {
    super(props)
    this.state = {
      activeCurrencyLabel: 'AUD',
      techProducts: [],
      isLoading: true,
      isMenuOpen: false,
      category: 'tech',
    }
  }
  onCurrencyMenuClick = (e) => {
    this.setState({ activeCurrencyLabel: e.currentTarget.textContent.split(' ')[1] })
  }

  componentDidMount = () => {
    this.props.client
      .query({
        query: gql`
          {
            category(input: { title: "${this.state.category}"}) {
              name
              products {
                id
                name
                inStock
                gallery
                description
                category
                attributes {
                  id
                  name
                  type
                  items {
                    displayValue
                    value
                    id
                  }
                }
                prices {
                  currency {
                    label
                    symbol
                  }
                  amount
                }
                brand
              }
            }
          }
        `,
      })
      .then((response) => {
        this.setState({ techProducts: response.data.category.products, isLoading: false })
      })
  }

  render() {
    const {
      onCurrencyMenuClick,
      props: { client },
      state: { activeCurrencyLabel, isLoading, techProducts },
    } = this
    const cartedProducts = techProducts.slice(0, 3)

    return (
      <Wrapper>
        <NavBar
          client={client}
          activeCurrencyLabel={activeCurrencyLabel}
          onCurrencyMenuClick={onCurrencyMenuClick}
        />
        <div className="content">
          {isLoading ? (
            <p>Loading...</p>
          ) : (
            <div>
              {cartedProducts.map((product) => {
                return (
                  <CartedProduct
                    key={product.id}
                    product={product}
                    activeCurrencyLabel={activeCurrencyLabel}
                    fullCartPageStyle={true}
                  />
                )
              })}
            </div>
          )}
        </div>
      </Wrapper>
    )
  }
}

export default CartPage
