import { PureComponent } from 'react'
import { NavBar } from 'src/components/NavBar'
import { AllProducts } from 'src/components/AllProducts'
import { TechProducts } from 'src/components/TechProducts'
import { ClothesProducts } from 'src/components/ClothesProducts'

import styled from 'styled-components'

const Wrapper = styled.div`
  .content {
    padding: 5px 50px;
    margin: 50px 0 ;
  }
`

export class HomePage extends PureComponent {
  constructor(props) {
    super(props)
    this.state = {

      activeLabel: 'AUD',
    }
  }
  onCurrencyMenuClick = (e) => {
    this.setState({ activeLabel: e.currentTarget.textContent.split(' ')[1] })
  }
  render() {
    const {
      onCurrencyMenuClick,
      props: { client },
      state: { activeLabel },
    } = this

    return (
      <Wrapper>
        <NavBar client={client} activeLabel={activeLabel} onCurrencyMenuClick={onCurrencyMenuClick}>
          <div label="all">
            <div className="content">
              <AllProducts client={client} />
            </div>
          </div>
          <div label="tech">
            <div className="content">
              <TechProducts client={client} />
            </div>
          </div>
          <div label="clothes">
            <div className="content">
              <ClothesProducts client={client} />
            </div>
          </div>
        </NavBar>
      </Wrapper>
    )
  }
}

export default HomePage
