import React, { Component } from 'react'
import PropTypes from 'prop-types'
import styled from 'styled-components'
import { gql } from '@apollo/client'

const Wrapper = styled.div`
  .carted-product__content {
    display: grid;
    grid-template-columns: 60% 37%;
    gap: 8px;
    margin-bottom: 40px;
    @media (max-width: ${(props) => props.theme.breakpoint.sm}) {
      display: flex;
      flex-direction: column-reverse;
    }

    .rhs {
      display: flex;
      gap: 8px;
    }
  }
  .carted-product__content-page {
    padding: 15px 0;
    border-bottom: 1px solid ${(props) => props.theme.colors.border_01};
    margin-bottom: 0px;
  }
  .carted-product__content-image {
    height: 190px;
    background-position: center;
    background-repeat: no-repeat;
    background-size: contain;
    max-width: 100%;
    position: relative;
    flex-grow: 1;
  }
  .details {
    display: flex;
    flex-direction: column;
    gap: 5px;
    justify-content: space-between;

    .text {
      color: ${(props) => props.theme.colors.text_01};
      font-size: 16px;
      line-height: 160%;
    }
    .price {
      span {
        font-weight: 500;
      }
    }
  }
  .boxes {
    display: flex;
    gap: 6px;
    .box {
      border: 1px solid ${(props) => props.theme.colors.text_01};
      min-width: 35px;
      text-align: center;
      font-size: 14px;
      padding: 7px;
      &.active {
        background-color: ${(props) => props.theme.colors.dark};
        transition: 0.3s;
        color: ${(props) => props.theme.colors.white};
      }
    }
    .color {
      width: 23px;
      height: 23px;
      min-width: auto;
    }
  }
  .quantity {
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    align-items: center;

    button {
      border: 1px solid ${(props) => props.theme.colors.text_01};
      background-color: ${(props) => props.theme.colors.white};
      width: 23px;
      font-size: 31px;
      line-height: 11px;
      height: 23px;
      line-height: 11px;
      &:hover {
        cursor: pointer;
        background-color: ${(props) => props.theme.colors.dark};
        transition: 0.3s;
        color: ${(props) => props.theme.colors.white};
      }
    }
  }
`

export class CartedProduct2 extends Component {
  static propTypes = {
    // product: PropTypes.object.isRequired,
    activeCurrencyLabel: PropTypes.string,
    cartFullPageStyles: PropTypes.bool,
  }
  constructor(props) {
    super(props)
    this.state = {
      productCount: 1,
      techProducts: [],
category:"tech"
    }
  }

  increaseProductCount = () => {
    this.setState({ productCount: this.state.productCount + 1 })
  }
  decreaseProductCount = () => {
    if (this.state.productCount > 1) {
      this.setState({ productCount: this.state.productCount - 1 })
    } else {
      this.setState({ productCount: this.state.productCount })
    }
  }
  componentDidMount = () => {
    this.props.client
      .query({
        query: gql`
          {
            category(input: { title: "${this.state.category}"}) {
              name
              products {
                id
                name
                inStock
                gallery
                description
                category
                attributes {
                  id
                  name
                  type
                  items {
                    displayValue
                    value
                    id
                  }
                }
                prices {
                  currency {
                    label
                    symbol
                  }
                  amount
                }
                brand
              }
            }
          }
        `,
      })
      .then((response) => {
        this.setState({ techProducts: response.data.category.products, isLoading: false })
      })
  }

  render() {
    const {
      increaseProductCount,
      decreaseProductCount,
      props: { activeCurrencyLabel, cartFullPageStyles = false },
      state: { productCount,techProducts },
    } = this
    const filterCurrentAmount = product?.prices.find((obj) => {
      return obj.currency.label === activeCurrencyLabel
    })

    const allColors = product.attributes.find((attribute) => attribute.name === 'Color')

    const allCapacities = product.attributes.find((attribute) => attribute.id === 'Capacity')
    const cartedProducts = techProducts
