import React, { Component } from 'react'
import PropTypes from 'prop-types'
import styled from 'styled-components'

const Wrapper = styled.div`
box-shadow: 0px 4px 35px rgba(168, 172, 176, 0.19);
background-color: ${(props) => props.theme.colors.white};
padding: 10px ;
max-height: 444px;

.product-card__content-text{
    margin-top:10px ;
    p{
        font-size: 18px;
line-height: 160%;
color: ${(props) => props.theme.colors.text_02};
font-weight: 300;

    }
    .product-card__content-text--price{
        font-weight:500 ;
        color: ${(props) => props.theme.colors.text_01};

    }
}

`

export class ProductCard extends Component {
  static propTypes = {
    product: PropTypes.object.isRequired,
  }

  render() {
    const {
      props: { product },
    } = this
    console.log(product.gallery[0])
    return (
      <Wrapper>
        <div className="product-card__content">
            <div className='product-card__content' style={{backgroundImage: `url(${product.gallery[0]})`}}>

            </div>
          <div className="product-card__content-text">
            <p className="product-card__content-text--name">{product.name}</p>
            <p className='product-card__content-text--price'>100

             </p>
             {/* {product.prices.currency.symbol}{product.prices.amount} */}
          </div>
        </div>
      </Wrapper>
    )
  }
}

export default ProductCard
