import React, { Component } from 'react'
import arrow from 'src/assets/images/arrow.svg'
import styled from 'styled-components'

const Wrapper = styled.div`
  .navbar__currency-menu {
    display: flex;
    cursor: pointer;
    gap: 8px;
    align-items: baseline;


    .navbar_currency-toggler {
      width: 10px;
      height: 5px;
      &.isActive {
      transform: rotate(-180deg);
    }
    }
  }
`
export class CurrrencyMenu extends Component {
  constructor(props) {
    super(props)
    // this.addActiveClass = this.addActiveClass.bind(this)
    this.state = {
      active: false,
    }
  }
  toggleClass = () => {
    const currentState = this.state.active
    this.setState({ active: !currentState })
  }
  render() {
    const {
      toggleClass,
      state: { active },
    } = this
    console.log(active)

    return (
      <Wrapper>
        <div className="navbar__currency-menu" onClick={toggleClass}>
          <p className="currency">$</p>
          <img
            className={`navbar_currency-toggler ${active ? 'isActive' : null}`}
            src={arrow}
            alt="arrow"
          />
        </div>
      </Wrapper>
    )
  }
}

export default CurrrencyMenu
