import React, { Component } from 'react'
import arrow from 'src/assets/images/arrow.svg'
import styled from 'styled-components'

const Wrapper = styled.div`
  position: relative;

  .navbar__currency-menu {
    display: flex;
    cursor: pointer;
    gap: 8px;
    align-items: baseline;

    .navbar_currency-toggler {
      width: 10px;
      height: 5px;
      &.isOpen {
        transform: rotate(-180deg);
      }
    }
  }
  .menu {
    position: absolute;
    padding: 10px 0;
    min-width: 118px;
    height: auto;
    background-color: ${(props) => props.theme.colors.white};
    display: none;
    box-shadow: rgb(0 0 0 / 10%) 0px 10px 24px;
    &.isShowing {
      display: block;
      left: -24px;
      top: 30px;
    }

    .menu__list {
      list-style-type: none;

      li {
        padding: 12px 21px;
        font-size: 14px;
        &:hover {
          background-color: ${(props) => props.theme.colors.grey_02};
        }
      }
    }

    .menu__list--active {
      background-color: ${(props) => props.theme.colors.grey_01};
    }
  }
`

const CURRENCIES_QUERY = `
{
  currencies {
      label
      symbol
  }
}
`

export class CurrrencyMenu extends Component {
  constructor(props) {
    super(props)
    this.state = {
      open: true,
      currencies: [],
      isFetching: false,
    }
  }

  componentDidMount = () => {
    this.setState({ ...this.state, isFetching: true })
    fetch('http://localhost:4000/graphql', {
      method: 'POST',
      headers: { 'content-type': 'application/json' },
      body: JSON.stringify({ query: CURRENCIES_QUERY }),
    })
      .then((response) => response.json())
      .then((data) => {
        this.setState({ currencies: data.data.currencies, isFetching: false })
      })
      .catch((e) => {
        console.log(e)
        this.setState({ ...this.state, isFetching: false })
      })
  }

  toggleCurrencyMenu = () => {
    const currentState = this.state.open
    this.setState({ open: !currentState })
  }

  render() {
    const {
      toggleCurrencyMenu,
      state: { open, currencies, isFetching },
    } = this
    console.log(currencies)
    console.log(typeof currencies)

    return (
      <Wrapper>
        <div className="navbar__currency-menu" onClick={toggleCurrencyMenu}>
          <p className="currency">$</p>
          <img
            className={`navbar_currency-toggler ${open ? 'isOpen' : ''}`}
            src={arrow}
            alt="arrow"
          />
        </div>
        <div className={`menu ${open ? 'isShowing' : null}`}>
          <ul className="menu__list">
            {currencies.map((currency) => {
              return (
                <li
                  className={`${currency.label === 'AUD' && 'menu__list--active'}`}
                  key={currency.symbol}
                >
                  {currency.symbol} {currency.label}
                </li>
              )
            })}
          </ul>
        </div>
      </Wrapper>
    )
  }
}

export default CurrrencyMenu
