import React, { Component } from 'react'
import PropTypes from 'prop-types'
import Tab from './Tab'
import styled from 'styled-components'
import logo from 'src/assets/images/logo.svg'
import cart from 'src/assets/images/cart.svg'
import arrow from 'src/assets/images/arrow.svg'

const Wrapper = styled.div`
  header {
    padding: 5px 50px;
    /* min-height: 80px; */
    display: flex;
  }
  .navbar {
    display: flex;
    gap: 10px;
    justify-content: space-between;
    align-items: center;
    width: 100%;
  }
  .navbar__tab-list {
    display: flex;
    cursor: pointer;
    list-style-type: none;
    li {
      padding: 18px 15px;
    }
  }

  .navbar__logo {
    width: 32px;
    height: 28px;
    cursor: pointer;
  }
  .navbar__currency-cart, .navbar__currency-menu{
      display: flex ;
    }

    .navbar__currency-cart{
      gap: 15px;
    }
    .navbar__currency-menu{
      gap: 5px;
      .navbar_currency-toggler{
        width: 10px;
height:5px ;
      }
    }
  .navbar__cart {
    position: relative;

    img {
      width: 27px;
      height: 20px;
      cursor: pointer;
    }

    .navbar__cart--items {
      background-color: ${(props) => props.theme.colors.dark};
      color: ${(props) => props.theme.colors.white};
      border-radius: 50%;
      width: 18px;
      justify-content: center;
      font-weight: bold;
      align-items: center;
      height: 18px;
      display: flex;
      font-size: 14px;
      position: absolute;
      right: -8px;
      top: -7px;
    }
  }
`
export class NavBar extends Component {
  static propTypes = {
    children: PropTypes.instanceOf(Array).isRequired,
  }

  constructor(props) {
    super(props)

    this.state = {
      activeTab: this.props.children[0].props.label,
    }
  }

  onClickTabItem = (tab) => {
    this.setState({ activeTab: tab })
  }

  onClickLogo = () => {
    this.setState({ activeTab: 'WOMEN' })
  }

  render() {
    const {
      onClickTabItem,
      onClickLogo,
      props: { children },
      state: { activeTab },
    } = this
    console.log(activeTab)

    return (
      <Wrapper>
        <div className="container">
          <header>
            <div className="navbar">
              <ul className="navbar__tab-list">
                {children.map((child) => {
                  const { label } = child.props
                  return (
                    <Tab activeTab={activeTab} key={label} label={label} onClick={onClickTabItem} />
                  )
                })}
              </ul>
              <img onClick={onClickLogo} className="navbar__logo" src={logo} alt="Brand logo" />
              <div className="navbar__currency-cart">
                <div className="navbar__currency-menu">
                  <p className="currency">$</p>
                  <img src={arrow} alt="arrow" className="navbar_currency-toggler" />
                </div>
                <div className="navbar__cart">
                  <img src={cart} alt="Brand cart" />
                  <p className="navbar__cart--items">3</p>
                </div>
              </div>
            </div>
          </header>

          <div className="tab-content">
            {children.map((child) => {
              if (child.props.label !== activeTab) return undefined
              return child.props.children
            })}
          </div>
        </div>
      </Wrapper>
    )
  }
}

export default NavBar
