import React, { Component } from 'react'
import styled from 'styled-components'
import cart from 'src/assets/images/cart.svg'
import { gql } from '@apollo/client'
import { CartedProduct } from 'src/components/CartedProduct'
import PropTypes from 'prop-types'
import { Link } from 'react-router-dom'
import { NavBar } from 'src/components/NavBar'

const Wrapper = styled.div`
  .content {
    padding: 5px 50px;
    margin: 125px 0 50px 0;
    @media (max-width: ${(props) => props.theme.breakpoint.sm}) {
      padding: 5px 25px;
    }
  }
  .navbar__cart {
    position: relative;

    img {
      width: 27px;
      height: 20px;
    }

    .navbar__cart--items {
      background-color: ${(props) => props.theme.colors.dark};
      color: ${(props) => props.theme.colors.white};
      border-radius: 50%;
      width: 18px;
      justify-content: center;
      font-weight: 500;
      align-items: flex-start;
      height: 18px;
      display: flex;
      font-size: 12px;
      position: absolute;
      right: -8px;
      top: -7px;
    }
  }

`
export class CartPage extends Component {
  static propTypes = {
    activeCurrencyLabel: PropTypes.string,
  }

  constructor(props) {
    super(props)
    this.state = {
      techProducts: [],
      isLoading: true,
      isMenuOpen: false,
      category: 'tech',
      activeCurrencyLabel: 'AUD',
    }
  }

  toggleCartMenu = () => {
    const currentState = this.state.isMenuOpen
    this.setState({ isMenuOpen: !currentState })
  }

  componentDidMount = () => {
    this.props.client
      .query({
        query: gql`
          {
            category(input: { title: "${this.state.category}"}) {
              name
              products {
                id
                name
                inStock
                gallery
                description
                category
                attributes {
                  id
                  name
                  type
                  items {
                    displayValue
                    value
                    id
                  }
                }
                prices {
                  currency {
                    label
                    symbol
                  }
                  amount
                }
                brand
              }
            }
          }
        `,
      })
      .then((response) => {
        this.setState({ techProducts: response.data.category.products, isLoading: false })
      })
  }

  render() {
    const {
      toggleCartMenu,
      // props: { activeCurrencyLabel },

      state: { isLoading, isMenuOpen, activeCurrencyLabel, techProducts },
    } = this
    const cartedProducts = techProducts.slice(0, 3)

    return (
      <Wrapper>
        <>
          <div className="content">
            <p className="heading">
              <span className="bold">My Bag, </span>
              <span className="light"> {cartedProducts.length} items</span>
            </p>
            {isLoading ? (
              <p>Loading...</p>
            ) : (
              <div>
                {cartedProducts.map((product) => {
                  return (
                    <CartedProduct
                      key={product.id}
                      product={product}
                      activeCurrencyLabel={activeCurrencyLabel}
                    />
                  )
                })}
              </div>
            )}
          </div>
          <div className="total">
            <p>Total</p>
            <p>$700.00</p>
          </div>

        </>
      </Wrapper>
    )
  }
}

export default CartPage
