import React, { Component } from 'react'
import styled from 'styled-components'

const Wrapper = styled.div``

const TECH_PRODUCTS_QUERY = `
{
    category(input: { title: "clothes" }) {
      name
      products{
        id
      }

    }
  }
`

export class ClothesProducts extends Component {
  constructor(props) {
    super(props)
    this.state = {
      clothesProducts: [],
      isFetching: false,
    }
  }
  componentDidMount = () => {
    this.setState({ ...this.state,isFetching: true })
    fetch('http://localhost:4000/graphql', {
      method: 'POST',
      headers: { 'content-type': 'application/json' },
      body: JSON.stringify({ query: TECH_PRODUCTS_QUERY}),
    })
      .then((response) => response.json())
      .then((data) => {
        this.setState({ clothesProducts: data, isFetching: false })
      })
      .catch((e) => {
        console.log(e)
        this.setState({ ...this.state, isFetching: false })
      })
  }

  render() {
    const {
      state: { isFetching, clothesProducts },
    } = this
    console.log(clothesProducts)

    return (
      <Wrapper>
        {isFetching ? (
          <p>Loading...</p>
        ) : (
          <div>
            ALL_PRODUCTS_QUERY
            {JSON.stringify(clothesProducts)}
          </div>
        )}
      </Wrapper>
    )
  }
}

export default ClothesProducts
