import { Products } from 'src/components/Products'
import PropTypes from 'prop-types'

export class AllProducts extends Component {
  static propTypes = {
    activeCurrencyLabel: PropTypes.string,
  }

  constructor(props) {
    super(props)
    this.state = {


      category: 'all',
    }
  }

  render() {
    const {
      props: { activeCurrencyLabel, client },
      state: { isLoading, allProducts, category },

    } = this

    return (
      <>
        <Products activeCurrencyLabel={activeCurrencyLabel} client={client} category={this.state.category}/>
      </>
    )
  }
}

export default AllProducts
