import React, { Component } from 'react'
import styled from 'styled-components'
import { gql } from '@apollo/client'

const Wrapper = styled.div``

export class ClothesProducts extends Component {
  constructor(props) {
    super(props)
    this.state = {
      clothesProducts: [],
      isLoading: true,
    }
  }
  componentDidMount = () => {
    const { client } = this.props

    client
      .query({
        query: gql`
          {
            category(input: { title: "clothes" }) {
              name
              products {
                id
                name
                inStock
                gallery
                description
                category
                attributes {
                  id
                  name
                  type
                  items {
                    displayValue
                    value
                    id
                  }
                }
                prices {
                  currency {
                    label
                    symbol
                  }
                  amount
                }

                brand
              }
            }
          }
        `,
      })
      .then((response) => {
        this.setState({ clothesProducts: response, isLoading: false })
      })
  }

  render() {
    const {
      state: { isLoading, clothesProducts },
    } = this
    console.log(clothesProducts)

    return <Wrapper>{isLoading ? <p>Loading...</p> : <div>clothesProducts</div>}</Wrapper>
  }
}

export default ClothesProducts
