import React from 'react';
import { Query } from "react-apollo";
import gql from "graphql-tag";
const Products = () => (
  <Query
    query={gql`
      {
        category {
            name
            products{
              id
              name
            }
          }
      }
    `}
  >
    {({ loading, error, data }) => {
        console.log(data)
      if (loading) return <p>Loading...</p>;
      if (error) return <p>Error :(</p>;
      return data.category.products.map(({ id, title, author, description, topic, url }) => (
        <div key={id}>
          <p>{`${title} by ${author}`}</p>
        </div>
      ));
    }}
  </Query>
);
export default Products;
