import React, { Component } from 'react'
import styled from 'styled-components'
import { gql } from '@apollo/client'

const Wrapper = styled.div``

export class AllProducts extends Component {
  constructor(props) {
    super(props)
    this.state = {
      allProducts: [],
      isFetching: true,
    }
  }

  componentDidMount = () => {
    this.props.client
      .query({
        query: gql`
          {
            category {
              name
              products {
                id
                name
                inStock
                gallery
                description
                category
                attributes {
                  id
                  name
                  type
                  items {
                    displayValue
                    value
                    id
                  }
                }
                prices {
                  currency {
                    label
                    symbol
                  }
                  amount
                }

                brand
              }
            }
          }
        `,
      })
      .then((response) => {
        this.setState({ allProducts: response.data.category.products, isFetching: false })
      })
  }

  render() {
    const {
      state: { isFetching, allProducts },
    } = this
    console.log(allProducts)

    return (
      <Wrapper>{isFetching ? <p>Loading...</p> : <div>allProducts</div>}</Wrapper>
    )
  }
}

export default AllProducts
