// import PropTypes from 'prop-types'
import React, { Component } from 'react'
import { gql } from '@apollo/client'

export class SingleProduct extends Component {

  constructor(props) {
    super(props)
    this.state = {
      singleProduct: {},
      isLoading: true,
    }
  }
  componentDidMount = () => {
    const searchParams = new URLSearchParams(window.location.search)
      const paramsObj = Object.fromEntries(searchParams)

    this.props.client
      .query({
        query: gql`
        {
          product(id: "${paramsObj.id}") {
            id
            name
            inStock
            gallery
            description
            category
            attributes {
              id
              name
              type
              items {
                displayValue
                value
                id
              }
            }
            prices {
              currency {
                label
                symbol
              }
              amount
            }

            brand
          }
        }`,
      })
      .then((response) => {
        this.setState({ singleProduct: response.data, isLoading: false })
      })
  }

  render() {
    const{
      state: { isLoading, singleProduct },

    }=this

    console.log(singleProduct.product.name)

    return (
      <div>{singleProduct.name}</div>
    )
  }
}

export default SingleProduct
