import React from "react";
import { ThemeProvider, createGlobalStyle } from "styled-components";
import PropTypes from "prop-types";


const theme = {
  color: {
    // ui_01: "#F47803",
    // ui_02: "#2EC4B6",
  },
  breakpoint: {
    lg: "1058px",
    md: "768px",
    sm: "480px",
  },
};

const GlobalStyle = createGlobalStyle`
@font-face {
  font-family: 'Satoshi';
  src: url('/fonts/Satoshi-Regular.otf');
  font-style: normal;
  font-weight: 400;
}

@font-face {
  font-family: 'Satoshi';
  src: url('/fonts/Satoshi-Medium.otf');
  font-style: normal;
  font-weight: 500;
}

@font-face {
  font-family: 'Satoshi';
  src: url('/fonts/Satoshi-Bold.otf');
  font-style: normal;
  font-weight: bold;
}
html{
  scroll-behavior: smooth;
}

img{
  width: 100%;
  height: auto;
}

html,
body,
h1,
h2,
h3,
h4,
h5,
h6,
p,
input,
button, ul {
  padding: 0;
  margin: 0;
  font-family: Satoshi, sans-serif;
}

.container{
  max-width: 1600px;
    margin: auto;
}

a {
  color: inherit;
  text-decoration: none;
}

* {
  box-sizing: border-box;
	}
`;

const Theme = (props) => (
  <ThemeProvider theme={theme}>
    <div>
      <GlobalStyle />
      {props.children}
    </div>
  </ThemeProvider>
);
Theme.propTypes = {
  children: PropTypes.any.isRequired,
};

export { theme };
export default Theme;
