import React, { Component } from 'react'
import arrow from 'src/assets/images/arrow.svg'
import styled from 'styled-components'
import { gql } from '@apollo/client'

const Wrapper = styled.div`
  position: relative;

  .navbar__currency-menu {
    display: flex;
    cursor: pointer;
    gap: 8px;
    align-items: baseline;

    .navbar_currency-toggler {
      width: 10px;
      height: 5px;
      &.isOpen {
        transform: rotate(-180deg);
      }
    }
  }
  .menu {
    position: absolute;
    padding: 10px 0;
    min-width: 124px;
    height: auto;
    background-color: ${(props) => props.theme.colors.white};
    display: none;
    box-shadow: rgb(0 0 0 / 10%) 0px 10px 24px;
    &.isShowing {
      display: block;
      left: -24px;
      top: 30px;
    }

    .menu__list {
      list-style-type: none;

      li {
        padding: 12px 21px;
        font-size: 14px;
        &:hover {
          background-color: ${(props) => props.theme.colors.grey_02};
        }
      }
    }

    .menu__list--active {
      background-color: ${(props) => props.theme.colors.grey_01};
    }
  }
`

export class CurrrencyMenu extends Component {
  constructor(props) {
    super(props)
    this.state = {
      isMenuOpen: false,
      currencies: [],
      activeCurrency:{
        label: 'AUD',
        symbol: '$',
      }
    }
  }

  componentDidMount = () => {
    this.props.client
      .query({
        query: gql`
          {
            currencies {
              label
              symbol
            }
          }
        `,
      })
      .then((response) => {
        this.setState({ currencies: response.data.currencies })
      })
  }


  toggleCurrencyMenu = () => {
    const currentState = this.state.isMenuOpen
    this.setState({ isMenuOpen: !currentState })
  }

  render() {
    const {
      toggleCurrencyMenu,
      state: { isMenuOpen, currencies, activeCurrency: { label, symbol } },
    } = this
    console.log(currencies);

    return (
      <Wrapper>
        <div className="navbar__currency-menu" onClick={toggleCurrencyMenu}>
          <p className="currency">$</p>
          <img
            className={`navbar_currency-toggler ${isMenuOpen ? 'isOpen' : ''}`}
            src={arrow}
            alt="arrow"
          />
        </div>
        <div className={`menu ${isMenuOpen ? 'isShowing' : null}`}>
          <ul className="menu__list">
            {currencies.map((currency) => {
              return (
                <li
                  className={`${currency.label === 'AUD' && 'menu__list--active'}`}
                  key={currency.symbol}
                >
                  {currency.symbol} {currency.label}
                </li>
              )
            })}
          </ul>
        </div>
      </Wrapper>
    )
  }
}

export default CurrrencyMenu
