import React, { useState, useEffect } from 'react'
import { useRouter } from 'next/router'
import styled from 'styled-components'
import { Tab } from '@/src/components/ui'
import { FadeCSSTransition } from '@/src/components/transitions'
import slugify from 'underscore.string/slugify'
import clsx from 'clsx'

const TabsContainer = styled.div`
  .tabs {
    display: block;
    width: 100%;
  }
  .tab-list {
    display: flex;
    width: 100%;
    list-style: none;
    margin: 0;
    padding: 0;
    border-bottom: 0.2rem solid ${(props) => props.theme.colors.tabListBorder};
  }
  .tabs[align='left'] > .tab-list {
    text-align: right;
  }
  .tabs[align='right'] > .tab-list {
    text-align: right;
  }
  .tabs[align='center'] > .tab-list {
    text-align: center;
  }
  .tab-content {
    display: block;
    width: 100%;
    margin: 1rem 0;
  }
  .tabs--button {
    & > .tab-list {
      border: 0;
    }
    & > .tab-content {
      border: 2px solid ${(props) => props.theme.colors.tabContentBorder};
      background-color: ${(props) => props.theme.colors.white};
      padding: 1rem;
      margin: 0.5rem 0 1rem 0;
      border-radius: 4px;
    }
  }
`

export const TabType = {
  DEFAULT: 'tab',
  BUTTON: 'tab-button',
}

export const Tabs = (props) => {
  const router = useRouter()
  const { tabs = [], align, tabType } = props
  const [activeTabIndex, setActiveTabIndex] = useState(0)

  const setActiveTabByIndex = (tabIndex) => {
    if (tabIndex >= 0) setActiveTabIndex(tabIndex)
  }

  const setActiveTabByHash = (tabTitleHash) => {
    const activeTabIndex = tabs.findIndex((tab) => slugify(tab.title) === tabTitleHash)
    setActiveTabByIndex(activeTabIndex)
  }

  const setActiveTab = (tabTitle) => {
    const activeTabIndex = tabs.findIndex((tab) => tab.title === tabTitle)
    setActiveTabByIndex(activeTabIndex)
  }

  useEffect(() => {
    if (router.asPath.includes('#')) {
      const activeTabHash = router.asPath.split('#')[1] || null
      if (activeTabHash) setActiveTabByHash(activeTabHash)
    }
  }, [])

  const mappedTabs = tabs.map((tab, index) => {
    const { title } = tab
    return title ? (
      <Tab
        key={index}
        title={tab.title}
        isActive={String(activeTabIndex) === String(index)}
        onClick={() => setActiveTabByIndex(index)}
        tabClassName={tabType}
      />
    ) : null
  })

  const mappedTabsContent = tabs.map((tab, index) => {
    const { content } = tab
    const isActive = String(activeTabIndex) === String(index)
    const contentStyle = { display: `${isActive ? 'block' : 'none'}` }
    const injectedProps = {
      setActiveTabByIndex: (tabIndex) => setActiveTabByIndex(tabIndex),
      setActiveTabByHash: (tabTitleHash) => setActiveTabByHash(tabTitleHash),
      setActiveTab: (tabTitle) => setActiveTab(tabTitle),
    }

    return (
      <FadeCSSTransition key={index} in={isActive}>
        <div style={contentStyle}>
          {typeof content === 'function' ? content(injectedProps) : content || null}
        </div>
      </FadeCSSTransition>
    )
  })

  return (
    <TabsContainer>
      <div className={clsx('tabs', { 'tabs--button': tabType === TabType.BUTTON })} align={align}>
        <ul className="tab-list">{mappedTabs}</ul>
        <div className="tab-content">{mappedTabsContent}</div>
      </div>
    </TabsContainer>
  )
}

export default Tabs
