import React, { Component } from 'react'
import arrow from 'src/assets/images/arrow.svg'
import styled from 'styled-components'

const Wrapper = styled.div`
  .navbar__currency-menu {
    display: flex;
    cursor: pointer;
    gap: 8px;
    align-items: baseline;

    .navbar_currency-toggler {
      width: 10px;
      height: 5px;
      &.isOpen {
        transform: rotate(-180deg);
      }
    }
  }
  .menu {
    position: absolute;
    width: 114px;
    height: 169px;
    background-color: ${(props) => props.theme.colors.white};
    padding: 20px;
  }
`
export class CurrrencyMenu extends Component {
  constructor(props) {
    super(props)
    this.state = {
      open: false,
    }
  }
  toggleCurrencyMenu = () => {
    const currentState = this.state.open
    this.setState({ open: !currentState })
  }
  render() {
    const {
      toggleCurrencyMenu,
      state: { open },
    } = this
    console.log(open)

    return (
      <Wrapper>
        <div className="navbar__currency-menu" onClick={toggleCurrencyMenu}>
          <p className="currency">$</p>
          <img
            className={`navbar_currency-toggler ${open ? 'isOpen' : null}`}
            src={arrow}
            alt="arrow"
          />
          <div className={`menu ${open ? 'isShowing' : null}`}>
            <ul className="menu__list">
              <li>$ USD</li>
              <li>€ EUR</li>
              <li>¥ JPY</li>
            </ul>
          </div>
        </div>
      </Wrapper>
    )
  }
}

export default CurrrencyMenu
